public class FindLongestPalindromSubString {
    public static String expand(String A, int point1, int point2) {
        while (point1 >= 0 && point2 <= A.length() - 1 && A.charAt(point1) == A.charAt(point2)) {
            point1--;
            point2++;
        }
      // do point1-- and point2++ bcoz at time when loop going to false. means value of condition are above you condition.
        point1++;
        point2--;
        StringBuffer sb = new StringBuffer();
        while ((point1) <= (point2)) {
            sb.append(A.charAt(point1));
            point1++;
        }
        return sb.toString();
    }

    public static String longestPalindrome(String A) {
        // traverse the string find consider every chaacter as center and check l.hc and
        // r.hc are equal are not
        // string A = "amadamc"
        String finalAns = "";
        // for odd length string
        for (int i = 0; i < A.length(); i++) {
            String ans = expand(A, i, i);
            // System.out.println(ans);
            if (ans.length() > finalAns.length()) {
                finalAns = ans;
            }
        }
        // for even string lenght
        for (int i = 0; i < A.length(); i++) {
            String ans = expand(A, i, i + 1);
            // System.out.println(ans);
            if (ans.length() > finalAns.length()) {
                finalAns = ans;
            }
        }
        return finalAns;
    }

    public static void main(String[] args) {
        String A = "";
        System.out.println(A.length());
        System.out.println(longestPalindrome(A));
    }
}
